def get_average_age(users):
    if len(users) == 0:
        return 0
    total_age = 0
    for user in users:
        total_age += user[1]
    return total_age / len(users)

def get_unique_cities(users):
    cities = set()
    for user in users:
        cities.add(user[2])
    return list(cities) 

def get_sorted_by_age(users):
    users.sort(key=lambda user: user[1])
    return users

def is_user_in_list(users, name):
    for user in users:
        if user[0] == name:
            return True
    return False

def get_filtered_users_by_age_and_city(users, age_limit, city):
    filtered_users = []
    for user in users:
        if user[1] > age_limit and user[2] == city:
            filtered_users.append(user)
    return filtered_users


